from flask import Flask
from flask import request


PORT=5000

app = Flask(__name__)

@app.route("/")
def index():
    headers = request.headers
    auth = headers.get("API")
    if auth == "hola":
        return """
        <html>
            <body>
                <h1>Pasaste</h1>
                <img src="data:image/jpeg;base64," alt="Messi sonriendo" style="width:300px;">
            </body>
        </html>
        """, 200
    else:
        return "Error", 500, {"Pista-Header": "Tenes que usar el header con API=\"hola\""}